import React from "react";
import styled from "styled-components";
import { getFirestore } from "../firebase/firebaseConfig";

import { Link } from "react-router-dom";



const Song = ({ currentSong }) => {
	const { name } = currentSong;
	//****** */

	useEffect(() => {
	  const getInfo = () => {
		setLoading(true);
		const db = getFirestore();
		const itemCollection = db.collection("items");
		itemCollection
		  .get()
		  .then((informationBaseData) => {
			if (informationBaseData.size === 0) {
			  console.log("No Results");
			}
			setLoading(false);
			const products = informationBaseData.docs.map((doc) => {
			  return { id: doc.id, ...doc.data() };
			});
			if (productCategory) {
			  const filterProducts = products.filter(
				(item) => item.category === productCategory
			  );
			  setProducts(filterProducts);
			} else {
			  setProducts(products);
			}
		  })
		  .catch((error) => {
			console.error("Failed to bring products", error);
		  });
	  };
	  getInfo();
	}, [productCategory]);
	//********* */
	return (
		<SongContainer>
			<Img src={currentSong.cover} alt={currentSong.name}></Img>
			<H1>{currentSong.name}</H1>
			<H2>{currentSong.artist}</H2>
		</SongContainer>
	);
};

const SongContainer = styled.div`
	margin-top: 10vh;
	min-height: 50vh;
	max-height: 60vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
`;

const Img = styled.img`
	width: 20%;
	border-radius: 50%;
	@media screen and (max-width: 768px) {
		width: 50%;
	}
`;

const H1 = styled.h2`
	padding: 3rem 1rem 1rem 1rem;
`;

const H2 = styled.h3`
	font-size: 1rem;
`;

export default Song;
